<template>
  <section v-if="show" class="whiteboard-stroke-picker">
    <span class="stroke-heading">Stroke</span>
    <div class="stroke-input">
      <input type="number" v-model="strokeWidth">
      <span>px</span>
    </div>
  </section>
</template>

<script>
import {ref, reactive, computed, onMounted} from 'vue';
import {useWhiteboardStore} from "../store/whiteboard";
import {useStrokeStore} from "../store/stroke";

const {toolSelected} = useWhiteboardStore();

export default {
  name: 'WhiteboardStrokePicker',
  setup() {
    const {strokeWidth} = useStrokeStore();

    const show = computed(() => {
      // only show when tool is pencil, rectangle or circle
      return ['pencil', 'rectangle', 'circle'].includes(toolSelected.value.toLowerCase());
    });

    return {
      show,
      strokeWidth,
    }
  },
  props: {},

}
</script>

