import {useWhiteboardStore} from "../../../store/whiteboard";
import {useWhiteboardEventListeners} from "../whiteboard";
import {onToolChange} from "../tool/on-tool-change";
import usePan from "../../tools/usePan";

const {toolSelected} = useWhiteboardStore();
const {onPanMouseDown, onPanMouseUp} = usePan();

let currentTool;

const onMiddleMouseDown = (event) => {
  console.log('middlemouse')
  currentTool = toolSelected.value;
  //  activate pan tool
  onToolChange('pan');
  onPanMouseDown();
}
const onMiddleMouseUp = () => {
  console.log('middlemouse')
  // deactivate pan tool, revert back to original tool
  console.log({currentTool});
  onToolChange(currentTool);
  onPanMouseUp();
}
export function useMiddleMouseListener() {
  return {
    onMiddleMouseDown,
    onMiddleMouseUp,
  }
}

