import {useOnAlt} from "../modifier/on-alt";

const {onAltRightClickMove} = useOnAlt();

const onRightClickDown = (event) => {
  console.log('right click down');
}

const onRightClickUp = (event) => {
  console.log('right click up');

}

const onRightClickMove = (event) => {
  console.log('right click move');

  if(event.evt.altKey){
    return onAltRightClickMove(event);
  }

}


export const useRightClickListener = () => {
  return {
    onRightClickDown,
    onRightClickMove,
    onRightClickUp
  }
}
