import { reactive, ref, computed } from 'vue';
import pencilSvg from '../../assets/cursor/pencil2.svg';
import textSvg from '../../assets/cursor/format-text.svg';
import {useWhiteboardStore} from "../../store/whiteboard";

export default () => {


  // return style based on tool selected
  const cursor = computed(() => {
    const {toolSelected} = useWhiteboardStore();
    console.log(toolSelected.value);
    switch (toolSelected.value?.toLowerCase()){
      case 'pencil':
        return 'url(' + pencilSvg + '), auto';
      case 'text':
        return 'url(' + textSvg + '), auto';
      case 'line':
        return 'crosshair';
      case 'pan':
        return 'grabbing';
      case 'rectangle':
        return "crosshair";
      default:
        return 'auto';
    }
  });
  return {
    cursor
  }



}
