
import {useWhiteboardStore} from "../../store/whiteboard";
import {useHistoryStore} from "../../store/history";

const { layer } = useWhiteboardStore();
const { record } = useHistoryStore();

const clearAll = () => {
  layer.value.destroyChildren();
  record();
  layer.value.draw();
}

export default () => {
  return {
    clearAll
  }
}
