import {addOutline, removeOutline} from "../stroke";
import {useWhiteboardStore} from "../../store/whiteboard";
import useCursorStyle from "../../hooks/styles/useCursorStyle";

const {stage} = useWhiteboardStore();
const {cursor: cursorStyle} = useCursorStyle();

const addOutlineOnHover = (shape, cursorTypeOnHover = null) => {
  shape.on('mouseenter', () => {
    stage.value.container().style.cursor = !cursorTypeOnHover ? cursorStyle.value : cursorTypeOnHover;
    addOutline(shape);
  });
  shape.on('mouseleave', () => {
    stage.value.container().style.cursor = cursorStyle.value
    removeOutline(shape);
  });
  return shape;
}

export {addOutlineOnHover}
