import { useWhiteboardStore } from "../store/whiteboard";

const { layer } = useWhiteboardStore();

const addOutline = (object) => {
  object.stroke('blue');
  object.strokeWidth(1);
  layer.value.draw();
}
const removeOutline = (object) => {
  object.strokeWidth(0);
  layer.value.draw();
}

export {
  addOutline,
  removeOutline
}
